<?$config['model_main']['table_name'] = 'berita';$config['model_main']['table_prikey'] = 'id_berita';$config['model_main']['query'] = array(    'query_table'=>array(	    'select'=>array('a.*,		(			select if(max(urutan)=a.urutan,				"max",				if(min(urutan)=a.urutan,					"min",					"false"				)			) from berita where id_menu=a.id_menu		) as urutans		,a.id_berita as id_manage,b.nama_penulis,c.menu_name',false),	    'from'=>array($config['model_main']['table_name'].' a'),  		array('method'=>'join','params'=>array('berita_penulis b','a.id_penulis=b.id_penulis')),  		array('method'=>'join','params'=>array('public_menu c','a.id_menu=c.id_menu')),  		array('method'=>'join','params'=>array('public_component d','c.id_component=d.id_component')),		'order_by'=>array('a.tanggal','desc'),		'order_by'=>array('a.urutan','asc'),		'where'=>array('d.component_name','artikel')    ),     'query_filter' => array(        'id_rubrik'=>array(            'type'=>'where'            ,'field'=>'a.id_menu'            ,'name'=>'id_rubrik'        ),    ),);?>