<?phpclass Model extends CI_Model {    function __construct (){        parent::__construct();		$this->table_name = 'public_banner';	}	function simpan() {		$oper = $this->input->post('oper');		switch ($oper):		// ====================== penamabhan data ==============================		case 'add':		    			$this->doset();			if($this->db->insert($this->table_name)):					$this->responce = array('result'=>'succes','message'=>'Data berhasil ditambahkan','oper'=>'add');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil ditambahkan','oper'=>'add');			endif;						break;		// ====================== pengeditan data ==============================		case 'edit':			$this->doset();			$this->db->where('id_banner',$this->input->post('id_banner'));			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil diubah','oper'=>'edit');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil diubah','oper'=>'edit');			endif;			break;		// ====================== penghapusan data ==============================		case 'del':		    // $this->query_action('delete',$params);			$this->db->where('id_banner',$this->input->post('id_banner'));			if($this->db->delete($this->table_name)):				$this->removeimage($this->input->post('id_banner'));				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus','oper'=>'del');			endif;			break;		// ====================== tidak ada oper ==============================		default:			$this->responce = array('result'=>'failed','message'=>'Cannot identify oper type','oper'=>$oper);		endswitch;		return $this->responce;	}	function doset(){				$urutan = $this->input->post('urutan');		if(strlen($urutan)==0)$urutan=$this->_getAutoIndex();		$this->db->set("urutan",$urutan);		$this->db->set("height",$this->input->post('height'));		$this->db->set("banner_judul",$this->input->post('banner_judul'));		$this->db->set("id_kanal",$this->input->post('id_kanal'));		$this->db->set("status",$this->input->post('status'));		$this->db->set("banner_link",$this->input->post('banner_link'));	}	function _getAutoIndex(){		$this->db->select_max('urutan', 'max');		$row = $this->db->get('public_banner')->row_array();		$new_index = 10;		if(!empty($row)):			$new_index = $row['max'] + 10;			return $new_index;		else:			return $new_index;		endif;	}	function optkanal($id_kanal=false){		$arr['default'] = false;		$this->db->where('level',1);		$data = $this->db->get('public_menu')->result();		$arr['options'] = array(0=>'Beranda');		foreach($data as $row):			if($row->id_menu==@$id_kanal){ $arr['default'] = $id_kanal; }			$arr['options'][$row->id_menu] = $row->menu_name;		endforeach;		return @$arr;		} 	function getdata($id=false){		$data = $this->db->where('id_banner',@$id)->get('public_banner')->row();		return @$data;	}	function saveupload(){		$id = $this->input->post("id_banner");		if(strlen($_FILES['banner_file']['name'])>0){			if(empty($id))$id=0;			if(!empty($id)){				$this->db->select('*');				$this->db->from('public_banner');				$this->db->where('id_banner',$id);				$row = $this->db->get()->row();				$file_path = PATH_BASE.'arsip/assets/media/file/banner/';				$old_file = $file_path.$row->banner_image;				if(is_file($old_file))unlink($old_file);			}			$result = $this->uploadFile($_FILES['banner_file'],$id);			if($result['status']=='error'){				echo "error#".$result['error']."#".$result['id_banner']."#";			}else{				echo "success#none#".$result['id_banner']."#";			}		}else{			echo "error#Tidak ada file#0#";		}							}	function uploadFile($file=false,$id=false){		$this->load->helper('file');		$path2 = PATH_BASE.'arsip/assets/media/file/banner/';		MakeDir($path2);		$config['upload_path'] = PATH_BASE.'arsip/assets/media/file/banner';		$config['allowed_types'] = 'jpg|png|gif|bmp|jpeg';		$config['max_size']	= '2048';		$config['remove_spaces']=true;        $config['overwrite']    =true;		$this->load->library('upload', $config);		if ( ! $this->upload->do_upload('banner_file'))		{			$data= array('status' => 'error', 'error' => $this->upload->display_errors());			return $data;		}			else		{			if(!empty($id)):				$this->db->set("banner_image", $this->upload->file_name);				$this->db->where("id_banner", $id);				$this->db->update("public_banner");			else:				$this->db->set("banner_image", $this->upload->file_name);				$this->db->insert("public_banner");				$id = $this->db->insert_id();			endif;			$data = array('status'=>'success','error' =>'', 'id_banner' => $id);			return $data;		}	}	function removeimage($id=false){		$this->db->select('*');		$this->db->from('public_banner');		$this->db->where('id_banner', $id);		$row = $this->db->get()->row();		$file_path = 'assets/media/file/banner/';		// $filethumb = $file_path.'_thumbs/thumbs_'.$row->banner_image;		$file = $file_path.$row->banner_image;		// if(is_file($filethumb))unlink($filethumb);		if(is_file($file))unlink($file);		$this->db->set("banner_image",'');		$this->db->where("id_banner",$id);		$this->db->update("public_banner");		}	function urutan($oper=false,$id_kanal=false,$urutan=false){		$urutan_new = '';		if($oper=="turun"){			$urutan_new = $urutan + 10;		}else{			$urutan_new = $urutan - 10;		}		// data self		$this->db->where('urutan',$urutan);		$this->db->where('id_kanal',$id_kanal);		$id_self = $this->db->get($this->table_name)->row()->id_banner;		// data will replaced		$this->db->where('urutan',$urutan_new);		$this->db->where('id_kanal',$id_kanal);		$id_replace = $this->db->get($this->table_name)->row()->id_banner;				$this->db->set('urutan',$urutan_new);		$this->db->where('id_banner',$id_self);		$this->db->update($this->table_name);		$this->db->set('urutan',$urutan);		$this->db->where('id_banner',$id_replace);		$this->db->update($this->table_name);	}}?>