<?phpclass Model extends CI_Model {    function __construct (){        parent::__construct();		$this->table_name = 'public_footer';	}	function simpan() {		$oper = $this->input->post('oper');		$id_footer = $this->input->post('id_footer');		switch ($oper):		// ====================== penamabhan data ==============================		case 'add':		    			if($this->input->post('status') == 'on'){				$this->db->set("status",'off');				$this->db->update("public_footer");			}			$this->db->set("footer_link",$this->input->post('footer_link'));			$this->db->set("status",$this->input->post('status'));			if($this->db->insert($this->table_name)):					$this->responce = array('result'=>'succes','message'=>'Data berhasil ditambahkan','oper'=>'add');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil ditambahkan','oper'=>'add');			endif;						break;		// ====================== pengeditan data ==============================		case 'edit':			if($this->input->post('status') == 'on'){				$this->db->set("status",'off');				$this->db->update("public_footer");			}			$this->db->set("footer_link",$this->input->post('footer_link'));			$this->db->set("status",$this->input->post('status'));			$this->db->where('id_footer',@$id_footer);			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil diubah','oper'=>'edit');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil diubah','oper'=>'edit');			endif;			break;		// ====================== penghapusan data ==============================		case 'del':			$this->db->where('id_footer',@$id_footer);			if($this->db->delete($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus','oper'=>'del');			endif;			break;		case 'refresh':			$status = $this->db->select('status')->where('id_footer',@$id_footer)->get($this->table_name)->row()->status;			if($status=='off'){				$this->db->set("status",'off');				$this->db->update("public_footer");				$status ='on';			}else{				$status ='off';			}			$this->db->set('status',@$status);			$this->db->where('id_footer',@$id_footer);			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data status berhasil ubah','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data status berhasil ubah','oper'=>'del');			endif;			break;		// ====================== tidak ada oper ==============================		default:			$this->responce = array('result'=>'failed','message'=>'Cannot identify oper type','oper'=>$oper);		endswitch;		return $this->responce;	}	function getdata($id=false){		$data = $this->db->where('id_footer',@$id)->get('public_footer')->row();		return @$data;	}	function saveupload(){		$id = $this->input->post("id_footer");		if(strlen($_FILES['footer_file']['name'])>0){			if(empty($id))$id=0;			if(!empty($id)){				$this->db->select('*');				$this->db->from('public_footer');				$this->db->where('id_footer',$id);				$row = $this->db->get()->row();				$file_path = PATH_BASE.'arsip/assets/media/file/footer/';				$old_file = $file_path.$row->footer_image;				if(is_file($old_file))unlink($old_file);			}			$result = $this->uploadFile($_FILES['footer_file'],$id);			if($result['status']=='error'){				echo "error#".$result['error']."#".$result['id_footer']."#";			}else{				echo "success#none#".$result['id_footer']."#";			}		}else{			echo "error#Tidak ada file#0#";		}							}	function uploadFile($file=false,$id=false){		$this->load->helper('file');		$config['upload_path'] = PATH_BASE.'arsip/assets/media/file/footer/';		$config['allowed_types'] = 'jpg|png|gif|bmp|jpeg';		$config['max_size']	= '2048';		$config['remove_spaces']=true;        $config['overwrite']    =true;		$this->load->library('upload', $config);		if ( ! $this->upload->do_upload('footer_file'))		{			$data= array('status' => 'error', 'error' => $this->upload->display_errors());			return $data;		}			else		{			if(!empty($id)):				$this->db->set("footer_image", $this->upload->file_name);				$this->db->where("id_footer", $id);				$this->db->update("public_footer");			else:				$this->db->set("footer_image", $this->upload->file_name);				$this->db->insert("public_footer");				$id = $this->db->insert_id();			endif;			$data = array('status'=>'success','error' =>'', 'id_footer' => $id);			return $data;		}	}	function removeimage($id=false){		$this->db->select('*');		$this->db->from('public_footer');		$this->db->where('id_footer', $id);		$row = $this->db->get()->row();		$file_path = 'assets/media/file/footer/';		$file = $file_path.$row->footer_image;		if(is_file($file))unlink($file);		$this->db->set("footer_image",'');		$this->db->where("id_footer",$id);		$this->db->update("public_footer");		}}?>