<?$config['model_main']['table_name'] = 'public_menu';$config['model_main']['table_prikey'] = 'id_menu';$config['model_main']['query'] = array(    'query_table'=>array(	    'select'=>array('a.*,		(			select if(max(menu_index)=a.menu_index,				"max",				if(min(menu_index)=a.menu_index,					"min",					"false"				)			) from public_menu where id_parent=a.id_parent and level=a.level		) as urutans		',false),	    'from'=>array($config['model_main']['table_name'].' a'),		'where'=>array('a.level',2),		'order_by'=>array('a.menu_index','asc')   ),    'query_filter' => array(        'id_menus'=>array(            'type'=>'where'            ,'field'=>'a.id_parent'            ,'name'=>'id_menus'        ),    ),);?>