<?$config['model_main']['table_name'] = 'pengumuman';$config['model_main']['table_prikey'] = 'id_pengumuman';$config['model_main']['query'] = array(    'query_table'=>array(	    'select'=>array('a.*,		(			select if(max(urutan)=a.urutan,				"max",				if(min(urutan)=a.urutan,					"min",					"false"				)			) from pengumuman where tanggal=a.tanggal		) as urutans'		,false),	    'from'=>array($config['model_main']['table_name'].' a'),		'order_by'=>array('a.tanggal','desc'),		'order_by'=>array('a.urutan','asc')    ), );?>