<?phpclass Model extends CI_Model {    function __construct (){        parent::__construct();		$this->table_name = 'm_perencanaan';	}	function simpan() {		$oper = $this->input->post('oper');		switch ($oper):		// ====================== penamabhan data ==============================		case 'add':		    			$this->doset();			if($this->db->insert($this->table_name)):					$this->responce = array('result'=>'succes','message'=>'Data berhasil ditambahkan','oper'=>'add');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil ditambahkan','oper'=>'add');			endif;						break;		// ====================== pengeditan data ==============================		case 'edit':			$this->doset();			$this->db->where('id_perencanaan',$this->input->post('id_perencanaan'));			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil diubah','oper'=>'edit');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil diubah','oper'=>'edit');			endif;			break;		// ====================== penghapusan data ==============================		case 'refresh':			$status = $this->input->post('status');			if($status=='publish'){				$status='pending';			}else{				$status='publish';			}			$this->db->set('status',@$status);			$this->db->where('id_perencanaan',$this->input->post('id_perencanaan'));			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus','oper'=>'del');			endif;			break;		// ====================== penghapusan data ==============================		case 'del':			$this->removefile($this->input->post('id_perencanaan'));			$this->db->where('id_perencanaan',$this->input->post('id_perencanaan'));			if($this->db->delete($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus','oper'=>'del');			endif;			break;		// ====================== tidak ada oper ==============================		default:			$this->responce = array('result'=>'failed','message'=>'Cannot identify oper type','oper'=>$oper);		endswitch;		return $this->responce;	}	function doset(){		$tanggal = $this->input->post('tanggal');		$tanggal = @$tanggal?@$tanggal:date('Y-m-d');		$this->db->set("judul",$this->input->post('judul'));		$this->db->set("tanggal",$tanggal);		$this->db->set("keterangan",$this->input->post('keterangan'));		$this->db->set("status",$this->input->post('status'));	}		function getdata($id=false){		$data = $this->db->where('id_perencanaan',@$id)->get('m_perencanaan')->row();		return @$data;	}	function saveupload(){		$id = $this->input->post("id_perencanaan");		if(strlen($_FILES['perencanaan_file']['name'])>0){			if(empty($id))$id=0;			if(!empty($id)){				$this->db->select('*');				$this->db->from('m_perencanaan');				$this->db->where('id_perencanaan',$id);				$row = $this->db->get()->row();				$file_path = PATH_BASE.'arsip/assets/media/file/perencanaan/';				$old_file = $file_path.$row->file;				if(is_file($old_file))unlink($old_file);			}			$result = $this->uploadFile($_FILES['perencanaan_file'],$id);			if($result['status']=='error'){				echo "error#".$result['error']."#".$result['id_perencanaan']."#";			}else{				echo "success#none#".$result['id_perencanaan']."#";			}		}else{			echo "error#Tidak ada file#0#";		}			}	function uploadFile($file=false,$id=false){		$this->load->helper('file');		$path2 = PATH_BASE.'arsip/assets/media/file/perencanaan/';		MakeDir($path2);		$config['upload_path'] = PATH_BASE.'arsip/assets/media/file/perencanaan';		$config['allowed_types'] = 'pdf|zip|rar|doc|docx|xls|xlsx';		$config['max_size']	= '2048';		$config['remove_spaces']=true;        $config['overwrite']    =true;		$this->load->library('upload', $config);		if ( ! $this->upload->do_upload('perencanaan_file'))		{			$data= array('status' => 'error', 'error' => $this->upload->display_errors());			return $data;		}			else		{			if($id > 0 ):				$this->db->set("file",$this->upload->file_name);				$this->db->where("id_perencanaan",@$id);				$this->db->update("m_perencanaan");			else:				$this->db->set("file",$this->upload->file_name);				$this->db->set("status",$this->input->post('status'));				$this->db->insert("m_perencanaan");				$act = 'edit';				$id = $this->db->insert_id();							endif;			$data = array('status'=>'success','error' =>'', 'id_perencanaan' => $id);			return $data;		}	}	function removefile($id=false){		$this->db->select('*');		$this->db->from('m_perencanaan');		$this->db->where('id_perencanaan',$id);		$row = $this->db->get()->row();		$file_path = 'assets/media/file/perencanaan/';		$file = $file_path.$row->file;		if(is_file($file))unlink($file);		$this->db->set("file",'');		$this->db->where("id_perencanaan",$id);		$this->db->update("m_perencanaan");		}}?>