<?phpclass Model extends CI_Model {    function __construct (){        parent::__construct();		$this->table_name = 'public_template';	}	function simpan() {		$oper = $this->input->post('oper');		switch ($oper):		// ====================== penamabhan data ==============================		case 'add':		    			$this->doset();			if($this->db->insert($this->table_name)):					$this->responce = array('result'=>'succes','message'=>'Data berhasil ditambahkan','oper'=>'add');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil ditambahkan','oper'=>'add');			endif;						break;		// ====================== pengeditan data ==============================		case 'edit':			$this->doset();			$this->db->where('id_template',$this->input->post('id_template'));			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil diubah','oper'=>'edit');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil diubah','oper'=>'edit');			endif;			break;		// ====================== penghapusan data ==============================		case 'del':			$this->removefile($this->input->post('id_template'));			$this->db->where('id_template',$this->input->post('id_template'));			if($this->db->delete($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus','oper'=>'del');			endif;			break;		// ====================== perubahan status ==============================		case 'refresh':			$status = $this->db->select('status')->where('id_template',$this->input->post('id_template'))->get($this->table_name)->row()->status;			if($status=='off'){				$this->db->set("status",'off');				$this->db->update($this->table_name);				$status ='on';			}else{				$status ='off';			}			$this->db->set('status',@$status);			$this->db->where('id_template',$this->input->post('id_template'));			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus','oper'=>'del');			endif;			break;		// ====================== tidak ada oper ==============================		default:			$this->responce = array('result'=>'failed','message'=>'Cannot identify oper type','oper'=>$oper);		endswitch;		return $this->responce;	}	function doset(){		$this->db->set("template_name",$this->input->post('template_name'));		$this->db->set("status",$this->input->post('status'));	}	function getdata($id=false){		$data = $this->db->where('id_template',@$id)->get('public_template')->row();		return @$data;	}	function saveupload(){		$id = $this->input->post("id_template");		if(strlen($_FILES['template_file']['name'])>0){			if(empty($id))$id=0;			if(!empty($id)){				$this->db->select('*');				$this->db->from('public_template');				$this->db->where('id_template',$id);				$row = $this->db->get()->row();				$file_path = DOC_PATH_ROOT . 'assets/media/file/template/';				$old_file = $file_path.$row->template_zip;				if(is_file($old_file))unlink($old_file);			}			$result = $this->uploadFile($_FILES['template_file'],$id);			if($result['status']=='error'){				echo "error#".$result['error']."#".$result['id_template']."#";			}else{				echo "success#none#".$result['id_template']."#";			}		}else{			echo "error#Tidak ada file#0#";		}			}	function uploadFile($file=false,$id=false){		$this->load->helper('file');		$path2 = DOC_PATH_ROOT . 'assets/media/file/template/';		MakeDir($path2);		$config['upload_path'] = DOC_PATH_ROOT . 'assets/media/file/template';		$config['allowed_types'] = 'zip';		$config['max_size']	= '2048';		$config['remove_spaces']=true;        $config['overwrite']    =true;		$this->load->library('upload', $config);		if ( ! $this->upload->do_upload('template_file'))		{			$data= array('status' => 'error', 'error' => $this->upload->display_errors());			return $data;		}			else		{			$dir_name = explode(".", $this->upload->file_name);			$this->load->library('unzip');			$fileName = DOC_PATH_ROOT . 'assets/media/file/template/'.$this->upload->file_name;			$targetDir = 'assets/templates/web/resources/'; 						$this->unzip->extract($fileName, $targetDir, $dir_name[0], true);						if($id > 0 ):				$this->db->set("template_dir",$dir_name[0]);				$this->db->set("template_zip",$this->upload->file_name);				$this->db->where("id_template",$id);				$this->db->update("public_template");			else:				$this->db->set("template_dir",$dir_name[0]);				$this->db->set("template_zip",$this->upload->file_name);				$this->db->insert("public_template");				$id = $this->db->insert_id();							endif;			$data = array('status'=>'success','error' =>'', 'id_template' => $id);			return $data;		}	}	function removefile($id=false){		$this->db->select('*');		$this->db->from('public_template');		$this->db->where('id_template',$id);		$row = $this->db->get()->row();		$file_path = 'assets/media/file/template/';		$file = $file_path.$row->template_zip;		if(is_file($file))unlink($file);		$this->db->set("template_zip",'');		$this->db->where("id_template",$id);		$this->db->update("public_template");		$path = 'assets/templates/web/resources/'.$row->template_dir;		$this->deleteDir($path);	}	function deleteDir($dir){		if (substr($dir, strlen($dir)-1, 1) != '/') 			$dir .= '/'; 		if ($handle = opendir($dir)) 	   	{ 	   		while ($obj = readdir($handle))        		{            		if ($obj != '.' && $obj != '..')            		{                		if (is_dir($dir.$obj))                		{                    		if (!$this->deleteDir($dir.$obj))                        		return false;                			}                		elseif (is_file($dir.$obj))                		{                    		if (!unlink($dir.$obj))     	                   	return false; 	               }            		}        		}        		closedir($handle);     		if (!@rmdir($dir))        			return false;        		return true;    		}    		return false; 	}}?>