<?phpclass Model_groupmenu extends CI_Model {    function __construct (){        parent::__construct();	}	function simpan(){        $id_menu = $this->input->post('id_menu');        $id_group = $this->input->post('id_group');        $cek = $this->input->post('cek');//        dump($cek);        $this->db->join('c_menu_acl b','b.id_menu=a.id_menu','left');        $this->db->where('b.id_menu',$id_menu);        $this->db->where('b.id_group',$id_group);        $group = $this->db->get('c_menu a')->row();                if(! @$cek){            $this->db->where('id_menu',$id_menu);            $this->db->where('id_group',$id_group);            $this->db->delete('c_menu_acl');            $this->responce = array('result'=>'succes','message'=>'Data berhasil diubah');            $id = $id_menu;            $this->log_pengguna->push('groupmenu','Daftar Menu Tiap Pengguna','edit',$id);        }else{            $this->db->set('id_menu',$id_menu);            $this->db->set('id_group',$id_group);            $this->db->insert('c_menu_acl');            $this->responce = array('result'=>'succes','message'=>'Data berhasil dirubah');            $id = $id_menu;            $this->log_pengguna->push('groupmenu','Daftar Menu Tiap Pengguna','edit',$id);        }		return $this->responce;	}}