<?$config['model_main']['table_name'] = 'arsip_data';$config['model_main']['table_prikey'] = 'id_data';$config['model_main']['query'] = array(    'query_table'=>array(	    'select'=>array('a.id_data,a.judul,a.no_arsip,a.tanggal,a.kodelama,b.name as sifat'),	    'from'=>array($config['model_main']['table_name'].' a'),		'join'=>array('arsip_sifat_cat b','a.id_sifat=b.id_sifat','left'),		// 'or_like'=>array('a.judul','IMB'),		// 'or_like'=>array('a.tahun','IMB')    ), );//print_r($_COOKIE);$ci =& get_instance(); $ci->session->sess_update();$term = $ci->session->userdata('p_term');if( !empty($term) ){	$config['model_main']['query']['query_table']['or_like'] = array(		array('a.judul',$term),		array('a.no_arsip',$term),		array('a.tahun',$term),		array('a.lokasi',$term)	);}//print_r($config['model_main']['query']['query_table']['or_like']);?>