<? $config['model_main']['table_name'] = 'public_menu';$config['model_main']['table_prikey'] = 'id_menu';$config['model_main']['query'] = array(    'query_table'=>array(	    'select'=>array('a.*,		b.id_component,b.component_name,		(			if(c.maxB=a.menu_index, "max", 				if(c.minB=a.menu_index, "min", "false")			)		) as urutans		',false),	    'from'=>array($config['model_main']['table_name'].' a'),  		array('method'=>'join','params'=>array('public_component b','a.id_component=b.id_component')),  		array('method'=>'join','params'=>array('(SELECT max(menu_index) as maxB,level, min(menu_index) as minB FROM public_menu GROUP BY `level`) c','a.level=c.level')),		'where'=>array('a.level',2),		'where'=>array('a.id_parent',1),		'order_by'=>array('a.menu_index','asc')    ), ); ?>