<?phpclass Model extends CI_Model {    function __construct (){        parent::__construct();		$this->table_name = 'public_menu';	}	function simpan() {		$oper = $this->input->post('oper');		$menu_index = $this->input->post('menu_index');		if(empty($menu_index))$menu_index=$this->_getAutoIndex();		switch ($oper):		// ====================== penamabhan data ==============================		case 'add':		    			$this->db->set("level",2);			$this->db->set("id_parent",1);			$this->db->set("id_component",$this->input->post('id_component'));			$this->db->set("menu_name",$this->input->post('menu_name'));			$this->db->set("menu_index",$menu_index);			$this->db->set("status",$this->input->post('status'));			if($this->db->insert($this->table_name)):					$this->responce = array('result'=>'succes','message'=>'Data berhasil ditambahkan','oper'=>'add');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil ditambahkan','oper'=>'add');			endif;						break;		// ====================== pengeditan data ==============================		case 'edit':			$this->db->set("menu_name",@$this->input->post('menu_name'));			$this->db->set("id_component",@$this->input->post('id_component'));			$this->db->set("menu_index",@$menu_index);			$this->db->set("status",@$this->input->post('status'));			$this->db->where('id_menu',@$this->input->post('id_menu'));			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil diubah','oper'=>'edit');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil diubah','oper'=>'edit');			endif;			break;		// ====================== penghapusan data ==============================		case 'del':			$this->db->where('id_menu',$this->input->post('id_menu'));			if($this->db->delete($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus, karena terdapat GROUP dalam KANAL ini!','oper'=>'del');			endif;			break;		// ====================== tidak ada oper ==============================		default:			$this->responce = array('result'=>'failed','message'=>'Cannot identify oper type','oper'=>$oper);		endswitch;		return $this->responce;	}	function getdata($id=false){		$data = $this->db->where('id_menu',@$id)->get('agenda')->row();		return @$data;	}	function optkomponen(){		$res = $this->db->order_by('component_name','asc')->get('public_component')->result_array();		$str_opt = '';		foreach($res as $row):			$str_opt .= $row['id_component'].':'.$row['component_name'].";";		endforeach;		$str_opt = substr($str_opt,0,strlen($str_opt)-1);		return $str_opt;	}	function _getAutoIndex(){		$this->db->select_max('menu_index', 'max');		$this->db->where('id_parent', 1);		$this->db->where('level', 2);		$row = $this->db->get('public_menu')->row_array();		$new_index = 10;		if(!empty($row)):			$new_index = $row['max'] + 10;			return $new_index;		else:			return $new_index;		endif;	}	function urutan($oper=false,$id_parent=false,$menu_index=false){		$urutan_new = '';		if($oper=="turun"){			$urutan_new = $menu_index + 10;		}else{			$urutan_new = $menu_index - 10;		}		// data self		$this->db->where('menu_index',$menu_index);		$this->db->where('id_parent',$id_parent);		$id_self = $this->db->get($this->table_name)->row()->id_menu;		// data will replaced		$this->db->where('menu_index',$urutan_new);		$this->db->where('id_parent',$id_parent);		$id_replace = $this->db->get($this->table_name)->row()->id_menu;				$this->db->set('menu_index',$urutan_new);		$this->db->where('id_menu',$id_self);		$up1 = $this->db->update($this->table_name);		$this->db->set('menu_index',$menu_index);		$this->db->where('id_menu',$id_replace);		$up2 = $this->db->update($this->table_name);				if($up1 && $up2){		    $this->db->select('a.*');		    return $this->db->where('a.id_parent',$id_parent)->order_by('a.menu_index')->get($this->table_name.' a')->result();		}	}}?>