<?phpclass Model extends CI_Model {    function __construct (){        parent::__construct();		$this->table_name = 'public_menu';	}	function simpan() {		$oper = $this->input->post('oper');		switch ($oper):		// ====================== penamabhan data ==============================		case 'add':		    			$menu_index = $this->input->post('menu_index');			if(empty($menu_index))$menu_index=$this->_getAutoIndex();			$this->db->set("level",1);			$this->db->set("id_parent",0);			$this->db->set("menu_name",$this->input->post('menu_name'));			$this->db->set("menu_index",$menu_index);			$this->db->set("id_component",'');			$this->db->set("menu_path",'');			$this->db->set("status",$this->input->post('status'));			if($this->db->insert($this->table_name)):					$this->responce = array('result'=>'succes','message'=>'Data berhasil ditambahkan','oper'=>'add');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil ditambahkan','oper'=>'add');			endif;						break;		// ====================== pengeditan data ==============================		case 'edit':			$this->db->set("menu_name",$this->input->post('menu_name'));			$this->db->set("menu_index",$this->input->post('menu_index'));			$this->db->set("status",$this->input->post('status'));			$this->db->where('id_menu',$this->input->post('id_menu'));			if($this->db->update($this->table_name)):				$this->responce = array('result'=>'succes','message'=>'Data berhasil diubah','oper'=>'edit');				$data = $this->db->get($this->table_name)->row();			    $this->responce['rows'] = @$data?$data:array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil diubah','oper'=>'edit');			endif;			break;		// ====================== penghapusan data ==============================		case 'del':			if($this->_cekGroupKanal($this->input->post('id_menu'))):				$this->	_delSubMenuKanal($this->input->post('id_menu'));				$this->db->where('id_menu',$this->input->post('id_menu'));				$this->db->delete($this->table_name);				$this->responce = array('result'=>'succes','message'=>'Data berhasil dihapus','oper'=>'del');				$this->responce['rows'] = array();			else:				$this->responce = array('result'=>'failed','message'=>'Data tidak berhasil dihapus, karena terdapat GROUP dalam KANAL ini!','oper'=>'del');			endif;			break;		// ====================== tidak ada oper ==============================		default:			$this->responce = array('result'=>'failed','message'=>'Cannot identify oper type','oper'=>$oper);		endswitch;		return $this->responce;	}	function getdata($id=false){		$data = $this->db->where('id_menu',@$id)->get('agenda')->row();		return @$data;	}	function _getAutoIndex(){		$this->db->select_max('menu_index', 'max');		$this->db->where('id_parent', 0);		$this->db->where('level', 1);		$row = $this->db->get('public_menu')->row_array();		$new_index = 10;		if(!empty($row)):			$new_index = $row['max'] + 10;			return $new_index;		else:			return $new_index;		endif;	}	function _delSubMenuKanal($id_parent){		//get sub menu		$this->db->select('*');		$this->db->where('id_parent', $id_parent);		$res = $this->db->get('public_menu')->result_array();		foreach($res as $row):			//cek child menu			$this->db->select_sum('id_menu','jml');			$this->db->where('id_parent', $row['id_menu']);			$rec = $this->db->get('public_menu')->row();			if($rec->jml > 0):				$this->_delSubMenuKanal($row['id_menu']);			else:				$this->db->where('id_menu', $row['id_menu']);				$this->db->delete('public_menu'); 			endif;		endforeach;			}	function _cekGroupKanal($id_kanal = 0){		$this->db->select('*');		$this->db->from('public_menu');		$this->db->where('level', 2);		$this->db->where('id_parent', $id_kanal);		$jml =  $this->db->count_all_results();		if($jml == 0):			return true;		else:			return false;		endif;	}	function urutan($oper=false,$id_parent=false,$menu_index=false){		$urutan_new = '';		if($oper=="turun"){			$urutan_new = $menu_index + 10;		}else{			$urutan_new = $menu_index - 10;		}		// data self		$this->db->where('menu_index',$menu_index);		$this->db->where('id_parent',$id_parent);		$id_self = $this->db->get($this->table_name)->row()->id_menu;		// data will replaced		$this->db->where('menu_index',$urutan_new);		$this->db->where('id_parent',$id_parent);		$id_replace = $this->db->get($this->table_name)->row()->id_menu;				$this->db->set('menu_index',$urutan_new);		$this->db->where('id_menu',$id_self);		$up1 = $this->db->update($this->table_name);		$this->db->set('menu_index',$menu_index);		$this->db->where('id_menu',$id_replace);		$up2 = $this->db->update($this->table_name);				if($up1 && $up2){		    $this->db->select('a.*');		    return $this->db->where('a.id_parent',$id_parent)->order_by('a.menu_index')->get($this->table_name.' a')->result();		}	}}?>